mapper.select:
select
`id`, `plan_id`, `farmer_id`, `visitor_id`, `visit_date`, `visit_content`, `problems_found`, `farmer_requests`, `follow_up_action`, `status`, `is_deleted`, `create_time`, `update_time`
from t_visit_record


        and `id` = #{id}


        and `plan_id` = #{planId}


        and `farmer_id` = #{farmerId}


        and `visitor_id` = #{visitorId}


        and `visit_date` = #{visitDate}


        and `visit_content` = #{visitContent}


        and `problems_found` = #{problemsFound}


        and `farmer_requests` = #{farmerRequests}


        and `follow_up_action` = #{followUpAction}


        and `status` = #{status}


        and `is_deleted` = #{isDeleted}


        and `create_time` = #{createTime}


        and `update_time` = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_visit_record


        and `id` = #{id}


        and `plan_id` = #{planId}


        and `farmer_id` = #{farmerId}


        and `visitor_id` = #{visitorId}


        and `visit_date` = #{visitDate}


        and `visit_content` = #{visitContent}


        and `problems_found` = #{problemsFound}


        and `farmer_requests` = #{farmerRequests}


        and `follow_up_action` = #{followUpAction}


        and `status` = #{status}


        and `is_deleted` = #{isDeleted}


        and `create_time` = #{createTime}


        and `update_time` = #{updateTime}

SELECT tvr.id, tvr.plan_id, tvr.farmer_id, tfp.head_name AS farmerName, tvr.visit_date, tvr.visit_content, tvr.create_time, tu.real_name as create_by FROM t_visit_record tvr LEFT JOIN t_farmer_profile tfp ON tvr.farmer_id = tfp.id LEFT JOIN t_user tu on tvr.visitor_id = tu.id WHERE tvr.is_deleted = 0
AND tvr.farmer_id = #{farmerId}
AND tfp.head_name LIKE CONCAT('%', #{farmerName}, '%')
AND tvr.visitor_id = #{visitorId}
AND tu.real_name LIKE CONCAT('%', #{userName}, '%')
ORDER BY tvr.visit_date DESC
